import{_ as i,c as a,a0 as e,o as r}from"./chunks/framework.P9qPzDnn.js";const m=JSON.parse('{"title":"Oh My Commit (Yet Another Auto Commit) - PRD","description":"","frontmatter":{},"headers":[],"relativePath":"product/prd.md","filePath":"product/prd.md"}'),t={name:"product/prd.md"};function o(h,l,n,u,s,d){return r(),a("div",null,l[0]||(l[0]=[e('<h1 id="oh-my-commit-yet-another-auto-commit-prd" tabindex="-1">Oh My Commit (Yet Another Auto Commit) - PRD <a class="header-anchor" href="#oh-my-commit-yet-another-auto-commit-prd" aria-label="Permalink to &quot;Oh My Commit (Yet Another Auto Commit) - PRD&quot;">​</a></h1><ul><li><a href="#产品定位">产品定位</a></li><li><a href="#核心优势">核心优势</a></li><li><a href="#设计理念">设计理念</a></li><li><a href="#目标用户">目标用户</a></li><li><a href="#核心功能">核心功能</a></li><li><a href="#用户界面">用户界面</a></li><li><a href="#技术路线">技术路线</a></li><li><a href="#性能指标">性能指标</a></li><li><a href="#开发里程碑">开发里程碑</a></li><li><a href="#成功指标">成功指标</a></li></ul><h2 id="产品定位" tabindex="-1">产品定位 <a class="header-anchor" href="#产品定位" aria-label="Permalink to &quot;产品定位&quot;">​</a></h2><p>Oh My Commit 是新一代的 Git 提交助手，专注于提供卓越的用户体验和强大的 AI 能力。我们的目标是让每一次代码提交都成为一种享受，而不是负担。</p><h2 id="核心优势" tabindex="-1">核心优势 <a class="header-anchor" href="#核心优势" aria-label="Permalink to &quot;核心优势&quot;">​</a></h2><ol><li><p><strong>极致的用户体验</strong></p><ul><li>基于 TypeScript + Tailwind CSS 构建的现代化界面</li><li>响应式设计，完美适配各种屏幕尺寸</li><li>流畅的动画过渡，提供沉浸式体验</li><li>键盘优先的操作设计，提升工作效率</li></ul></li><li><p><strong>强大的 AI 引擎</strong></p><ul><li>多模型支持架构，灵活切换不同 AI 服务</li><li>自研的代码理解引擎，深度分析变更内容</li><li>持续学习的提交优化系统，不断提升质量</li><li>团队风格适配，保持提交信息一致性</li></ul></li></ol><h2 id="设计理念" tabindex="-1">设计理念 <a class="header-anchor" href="#设计理念" aria-label="Permalink to &quot;设计理念&quot;">​</a></h2><ul><li><strong>简单至上</strong>：用户无需关心底层技术实现，只需关注结果</li><li><strong>灵活可选</strong>：提供多种提交方案，用户可根据成本、效果、性能自由选择</li><li><strong>用户友好</strong>：支持多行提交信息，方便用户详细描述变更内容</li><li><strong>一次配置</strong>：API 密钥等配置只需设置一次，后续专注于提交体验</li></ul><h2 id="目标用户" tabindex="-1">目标用户 <a class="header-anchor" href="#目标用户" aria-label="Permalink to &quot;目标用户&quot;">​</a></h2><ul><li>使用 VSCode 的开发者</li><li>重视代码提交质量的团队</li><li>希望提高工作效率的个人开发者</li></ul><h2 id="核心功能" tabindex="-1">核心功能 <a class="header-anchor" href="#核心功能" aria-label="Permalink to &quot;核心功能&quot;">​</a></h2><h3 id="_1-智能提交-重新设计" tabindex="-1">1. 智能提交（重新设计） <a class="header-anchor" href="#_1-智能提交-重新设计" aria-label="Permalink to &quot;1. 智能提交（重新设计）&quot;">​</a></h3><ul><li><p><strong>快速提交模式</strong></p><ul><li>一键式智能提交</li><li>使用推荐的默认提交方案</li><li>无需任何额外配置</li></ul></li><li><p><strong>提交方案选择</strong></p><ul><li>在状态栏快速切换不同提交方案</li><li>方案示例： <ul><li><code>official_recommend</code>（官方推荐，平衡性能与成本）</li><li><code>gcop_fast</code>（速度优先）</li><li><code>premium_quality</code>（质量优先）</li></ul></li><li>支持用户自定义方案名称</li><li>显示方案的成本/性能/质量指标</li></ul></li></ul><h3 id="_2-配置管理-重新设计" tabindex="-1">2. 配置管理（重新设计） <a class="header-anchor" href="#_2-配置管理-重新设计" aria-label="Permalink to &quot;2. 配置管理（重新设计）&quot;">​</a></h3><ul><li><p><strong>API 配置</strong></p><ul><li>统一的 API 配置界面</li><li>支持多个服务商的 API 设置</li><li>API 密钥安全存储</li><li>一键测试 API 可用性</li></ul></li><li><p><strong>方案管理</strong></p><ul><li>自定义提交方案</li><li>重命名已有方案</li><li>调整方案参数</li><li>导入/导出方案配置</li></ul></li></ul><h3 id="_3-代码变更分析" tabindex="-1">3. 代码变更分析 <a class="header-anchor" href="#_3-代码变更分析" aria-label="Permalink to &quot;3. 代码变更分析&quot;">​</a></h3><ul><li>智能识别重要代码变更</li><li>支持多文件变更的统一分析</li><li>识别重构、bugfix、feature 等不同类型的变更</li></ul><h3 id="_4-用户交互" tabindex="-1">4. 用户交互 <a class="header-anchor" href="#_4-用户交互" aria-label="Permalink to &quot;4. 用户交互&quot;">​</a></h3><ul><li>提供直观的 UI 界面</li><li>支持快捷键操作</li><li>提交前预览和编辑功能</li><li>实时反馈和建议</li></ul><h3 id="_5-高级特性" tabindex="-1">5. 高级特性 <a class="header-anchor" href="#_5-高级特性" aria-label="Permalink to &quot;5. 高级特性&quot;">​</a></h3><ul><li>支持 Git hooks 集成</li><li>团队配置共享</li><li>提交历史分析和建议</li><li>AI 模型可配置（支持自定义 API）</li></ul><h2 id="用户界面" tabindex="-1">用户界面 <a class="header-anchor" href="#用户界面" aria-label="Permalink to &quot;用户界面&quot;">​</a></h2><h3 id="_1-状态栏" tabindex="-1">1. 状态栏 <a class="header-anchor" href="#_1-状态栏" aria-label="Permalink to &quot;1. 状态栏&quot;">​</a></h3><ul><li>当前选择的提交方案名称</li><li>快速切换方案的下拉菜单</li><li>方案性能指标图标（速度/成本/质量）</li></ul><h3 id="_2-命令面板" tabindex="-1">2. 命令面板 <a class="header-anchor" href="#_2-命令面板" aria-label="Permalink to &quot;2. 命令面板&quot;">​</a></h3><ul><li><code>Oh My Commit: Quick Commit</code> - 使用当前方案快速提交</li><li><code>Oh My Commit: Select Model</code> - 切换提交方案</li><li><code>Oh My Commit: Open Preferences</code> -打开用户配置系统</li></ul><h3 id="_3-设置界面" tabindex="-1">3. 设置界面 <a class="header-anchor" href="#_3-设置界面" aria-label="Permalink to &quot;3. 设置界面&quot;">​</a></h3><ul><li>API 配置分组</li><li>提交方案管理</li><li>个性化设置</li></ul><h2 id="技术路线" tabindex="-1">技术路线 <a class="header-anchor" href="#技术路线" aria-label="Permalink to &quot;技术路线&quot;">​</a></h2><h3 id="phase-1-基础框架-已完成" tabindex="-1">Phase 1 - 基础框架（已完成） <a class="header-anchor" href="#phase-1-基础框架-已完成" aria-label="Permalink to &quot;Phase 1 - 基础框架（已完成）&quot;">​</a></h3><ul><li>✅ 搭建 TypeScript + Tailwind CSS 开发环境</li><li>✅ 实现基础 UI 组件库</li><li>✅ 集成 Jotai 状态管理</li><li>✅ 完成核心提交流程</li></ul><h3 id="phase-2-体验优化-进行中" tabindex="-1">Phase 2 - 体验优化（进行中） <a class="header-anchor" href="#phase-2-体验优化-进行中" aria-label="Permalink to &quot;Phase 2 - 体验优化（进行中）&quot;">​</a></h3><ul><li>🏃‍♂️ 优化 UI/UX 设计</li><li>🏃‍♂️ 实现快捷键系统</li><li>🏃‍♂️ 改进错误处理</li></ul><h3 id="phase-3-ai-增强-进行中" tabindex="-1">Phase 3 - AI 增强（进行中） <a class="header-anchor" href="#phase-3-ai-增强-进行中" aria-label="Permalink to &quot;Phase 3 - AI 增强（进行中）&quot;">​</a></h3><ul><li>📅 集成更多 AI 模型</li><li>📅 优化代码分析算法</li><li>📅 添加团队学习功能</li><li>📅 实现自定义训练</li></ul><h3 id="phase-4-生态建设-规划中" tabindex="-1">Phase 4 - 生态建设（规划中） <a class="header-anchor" href="#phase-4-生态建设-规划中" aria-label="Permalink to &quot;Phase 4 - 生态建设（规划中）&quot;">​</a></h3><ul><li>📅 提供 API 集成</li><li>📅 建设开发者社区</li><li>📅 推出企业版本</li></ul><h2 id="性能指标" tabindex="-1">性能指标 <a class="header-anchor" href="#性能指标" aria-label="Permalink to &quot;性能指标&quot;">​</a></h2><ul><li>快速提交响应时间 &lt; 2s</li><li>配置切换无延迟</li><li>API 配置过程简单直观</li></ul><h2 id="开发里程碑" tabindex="-1">开发里程碑 <a class="header-anchor" href="#开发里程碑" aria-label="Permalink to &quot;开发里程碑&quot;">​</a></h2><h3 id="phase-1-mvp-v0-1-0" tabindex="-1">Phase 1 - MVP（v0.1.0） <a class="header-anchor" href="#phase-1-mvp-v0-1-0" aria-label="Permalink to &quot;Phase 1 - MVP（v0.1.0）&quot;">​</a></h3><ul><li>[x] 项目初始化</li><li>[x] 基础 VSCode 插件框架</li><li>[ ] 简单的 AI 提交信息生成</li><li>[x] 基础 UI 界面</li></ul><h3 id="phase-2-核心功能-v0-2-0" tabindex="-1">Phase 2 - 核心功能（v0.2.0） <a class="header-anchor" href="#phase-2-核心功能-v0-2-0" aria-label="Permalink to &quot;Phase 2 - 核心功能（v0.2.0）&quot;">​</a></h3><ul><li>[ ] 完整的代码分析系统</li><li>[ ] 多种提交格式支持</li><li>[x] 用户配置系统</li><li>[ ] 提交历史分析</li></ul><h3 id="phase-3-高级特性-v0-3-0" tabindex="-1">Phase 3 - 高级特性（v0.3.0） <a class="header-anchor" href="#phase-3-高级特性-v0-3-0" aria-label="Permalink to &quot;Phase 3 - 高级特性（v0.3.0）&quot;">​</a></h3><ul><li>[ ] 团队功能</li><li>[x] CI/CD 集成</li><li>[ ] 性能优化</li><li>[ ] 多语言支持完善</li></ul><h2 id="成功指标" tabindex="-1">成功指标 <a class="header-anchor" href="#成功指标" aria-label="Permalink to &quot;成功指标&quot;">​</a></h2><ol><li>用户满意度 &gt; 4.5/5</li><li>VSCode 商店评分 &gt; 4.8/5</li><li>月活用户 &gt; 10,000</li><li>提交信息采纳率 &gt; 80%</li></ol>',48)]))}const P=i(t,[["render",o]]);export{m as __pageData,P as default};
